import { createApp } from 'vue'
import App from './App.vue'
import axios from 'axios'

// Check the hostname and set the base URL accordingly
const baseURL = window.location.hostname === 'localhost' ? 'http://localhost:8080/api' : `${window.location.origin}/api`;

axios.defaults.baseURL = baseURL;


createApp(App).mount('#app')
